import React, { useState } from 'react';
import axios from 'axios';

function UploadEmail({ onScanComplete }) {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [dragOver, setDragOver] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile && droppedFile.name.endsWith('.eml')) {
      setFile(droppedFile);
    }
  };

  const handleUpload = async () => {
    if (!file) return;

    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/upload_email', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      onScanComplete(response.data);
    } catch (error) {
      alert('Error uploading file: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>üìß Upload Email File for Analysis</h2>
      <div 
        className={`upload-area ${dragOver ? 'drag-over' : ''}`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        <div style={{fontSize: '3em', marginBottom: '20px'}}>
          üìÅ
        </div>
        <input
          type="file"
          accept=".eml"
          onChange={handleFileChange}
          style={{display: 'none'}}
          id="file-input"
        />
        <label htmlFor="file-input" style={{cursor: 'pointer', color: '#667eea', fontWeight: '600'}}>
          Choose .eml file or drag & drop here
        </label>
        {file && (
          <p style={{marginTop: '15px', color: '#28a745', fontWeight: '600'}}>
            ‚úì Selected: {file.name}
          </p>
        )}
        <p style={{color: '#6c757d', fontSize: '14px', marginTop: '10px'}}>
          Upload email files (.eml format) to analyze for phishing indicators
        </p>
        <button
          onClick={handleUpload}
          disabled={!file || loading}
        >
          {loading ? (
            <>
              <span className="loading">üîç Analyzing</span>
            </>
          ) : (
            'üöÄ Upload & Analyze'
          )}
        </button>
      </div>
    </div>
  );
}

export default UploadEmail;
