import React, { useState, useEffect } from 'react';
import axios from 'axios';

function EmailDetails({ emailId }) {
  const [email, setEmail] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchEmailDetails();
  }, [emailId]);

  const fetchEmailDetails = async () => {
    try {
      const response = await axios.get(`/email/${emailId}`);
      setEmail(response.data);
    } catch (error) {
      console.error('Error fetching email details:', error);
    } finally {
      setLoading(false);
    }
  };

  const getRiskClass = (score) => {
    if (score < 30) return 'risk-low';
    if (score < 70) return 'risk-medium';
    return 'risk-high';
  };

  if (loading) return <div>Loading...</div>;
  if (!email) return <div>Email not found</div>;

  return (
    <div className="email-details">
      <h2>Email Details</h2>

      <div className={`risk-score ${getRiskClass(email.ai_score)}`}>
        Risk Score: {email.ai_score}/100
      </div>

      <div className="email-field">
        <label>AI Summary:</label>
        <p>{email.ai_summary}</p>
      </div>

      <div className="email-field">
        <label>Scan Date:</label>
        <p>{new Date(email.timestamp).toLocaleString()}</p>
      </div>

      <div className="email-field">
        <label>From:</label>
        <p>{email.sender}</p>
      </div>

      <div className="email-field">
        <label>Subject:</label>
        <p>{email.subject}</p>
      </div>

      <div className="email-field">
        <label>Body:</label>
        <p style={{whiteSpace: 'pre-wrap'}}>{email.body}</p>
      </div>

      {email.urls.length > 0 && (
        <div className="email-field">
          <label>URLs Found:</label>
          <ul>
            {email.urls.map((url, index) => (
              <li key={index}>{url}</li>
            ))}
          </ul>
        </div>
      )}

      {email.attachments.length > 0 && (
        <div className="email-field">
          <label>Attachments:</label>
          <ul>
            {email.attachments.map((att, index) => (
              <li key={index}>{att.filename} ({att.content_type})</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default EmailDetails;
